var x = 5;

if (x > 0) {
  text('x is a positive number!', 200, 200);
}

var x = 2 + 2; // Sets it equal to 4

if (x === 4) { // Asks the question, "does this equal 4?"
   text("yep, 2 + 2 = 4!", 200, 200);
}

